int main(){
    ios_base::sync_with_stdio(0);
    cin.tie(0);cout.tie(0);
    scanf("%d%d%d",&n,&m,&k);
    int arr[n+1][m+1] , srted[n+1][m+1];
    for(int i=0;i<n;i++)
    {
        for(int j=0;j<m;j++)
            scanf("%d",&arr[i][j]) , srted[i][j]=arr[i][j];
        if(k)sort(srted[i],srted[i]+m,greater<int>());
        else sort(srted[i],srted[i]+m);
    }
    for(int j=0;j<m;j++)
        for(int i=0;i<n;i++)
        {
            if(arr[i][j]==srted[i][j])continue;
            for(int l=j+1;l<m;l++)
            {
                if(srted[i][j]==arr[i][l])
                {
                    for(int r=0;r<n;r++)
                    {
                        if(k && arr[r][j]<arr[r][l])
                            swap(arr[r][j],arr[r][l]);
                        else if(!k &&arr[r][j]>arr[r][l])
                            swap(arr[r][j],arr[r][l]);
