#include<bits/stdc++.h>

using namespace std;

#define FOR(i,j,k) for(int i=j;i<=k;i++)

typedef unsigned long long ULL;
typedef pair<int,int> pii;

const int MAX = (1e6) + 10;
int lf[MAX], rg[MAX], lev[MAX], bigChild[MAX], sz[MAX], dfa[MAX], ti;
int cnt[MAX];
vector<int> mat[MAX];

void dfs(int p,int pa)
{
    lf[p] = ++ti; sz[p] = 1;
    dfa[ti] = p;

    int mx = -1;
    bigChild[p] = -1;

    for(auto x : mat[p]) {
        if(x != pa) {
            lev[x] = lev[p] + 1;
            dfs(x, p);
            sz[p] += sz[x];
            if(sz[x] > mx) mx = sz[x], bigChild[p] = x;
        }
    }
    rg[p] = ti;
}

int prin = 0, dekh[MAX];

void operationOnNode(int v,int value) {

    cnt[ lev[v] ] += value;

    if(cnt[ lev[v] ] > cnt[prin]) {
        prin = lev[v];
    } else if(cnt[ lev[v] ] == cnt[prin] && lev[v] < prin) {
        prin = lev[v];
    }
}

void update(int p,int pa,int pop,int value) {
    operationOnNode(p, value);

    for(int x : mat[p]) {
        if(x != pa && x != pop) {

            FOR(i,lf[x],rg[x]) {
                operationOnNode(dfa[i], value);
            }
        }
    }

    if(value == -1) prin = 0;
}

void dsu(int p,int pa,int keep)
{
    for(auto x : mat[p])
        if(x != pa && x != bigChild[p])
            dsu(x, p, 0);

    if(bigChild[p] != -1)
            dsu(bigChild[p], p, 1);

    update(p, pa, -1, 1);

    dekh[p] = prin - lev[p];

    if(!keep) {
        update(p, pa, -1, -1);
    }
}

int main()
{
    ios::sync_with_stdio(false);
    //freopen("in.txt", "r", stdin);

    int n, q, x, y, root;

    cin >> n;
    FOR(i,1,n)
    {
        cin >> x >> y;

        mat[x].push_back(y);
        mat[y].push_back(x);
    }

    root = 1;
    dfs(root, root);
    dsu(root, root, 0);

    FOR(i,1,n) cout << dekh[i] << "\n";
    return 0;
}

