public static int[] linearNormalization(int[] matrix) {
    int[] result = new int[matrix.length];
    int min = java.lang.Integer.MAX_VALUE;
    int max = java.lang.Integer.MIN_VALUE;
    for (int i = 0; i < (matrix.length); i++) {
        min = java.lang.Math.min(min, matrix[i]);
        max = java.lang.Math.max(max, matrix[i]);
    }
    int delta = max - min;
    while (min < 0) {
        max += delta;
        min += delta;
    } 
    double scale = 255.0 / delta;
    int desp = min;
    if (delta <= 255) {
        scale = 1;
        desp = max - 255;
    }
    if (max <= 255)
        desp = 0;

    for (int i = 0; i < (matrix.length); i++) {
        result[i] = ((int) (java.lang.Math.round((scale * ((matrix[i]) - desp)))));
    }
    return result;
}

