    java.util.Set<org.wso2.carbon.apimgt.impl.API> apiSet = new java.util.TreeSet<org.wso2.carbon.apimgt.impl.API>(new org.wso2.carbon.apimgt.impl.APINameComparator());
    boolean isTenantFlowStarted = false;
    try {
        if ((requestedTenant != null) && (!(MultitenantConstants.SUPER_TENANT_DOMAIN_NAME.equals(requestedTenant)))) {
            org.wso2.carbon.context.PrivilegedCarbonContext.startTenantFlow();
            org.wso2.carbon.context.PrivilegedCarbonContext.getThreadLocalCarbonContext().setTenantDomain(requestedTenant, true);
            isTenantFlowStarted = true;
        }
        java.lang.String resourceByTagQueryPath = (RegistryConstants.QUERIES_COLLECTION_PATH) + "/resource-by-tag";
        java.util.Map<java.lang.String, java.lang.String> params = new java.util.HashMap<java.lang.String, java.lang.String>();
        params.put("1", tag);
        params.put(RegistryConstants.RESULT_TYPE_PROPERTY_NAME, RegistryConstants.RESOURCE_UUID_RESULT_TYPE);
        java.util.Collection collection = registry.executeQuery(resourceByTagQueryPath, params);
        org.wso2.carbon.governance.api.generic.GenericArtifactManager artifactManager = org.wso2.carbon.apimgt.impl.APIUtil.getArtifactManager(registry, APIConstants.API_KEY);
        for (java.lang.String row : collection.getChildren()) {
            java.lang.String uuid = row.substring(((row.indexOf(';')) + 1), row.length());
            try {
                org.wso2.carbon.governance.api.generic.dataobjects.GenericArtifact genericArtifact = artifactManager.getGenericArtifact(uuid);
                if ((genericArtifact != null) && (APIConstants.PUBLISHED.equals(genericArtifact.getAttribute(APIConstants.API_OVERVIEW_STATUS)))) {
                    org.wso2.carbon.apimgt.impl.API api = org.wso2.carbon.apimgt.impl.APIUtil.getAPI(genericArtifact);
                    if (api != null) {
                        apiSet.add(api);
                    }
                }
            } catch (org.wso2.carbon.registry.core.exceptions.RegistryException e) {
                org.wso2.carbon.apimgt.impl.APIConsumerImpl.log.warn(("User is not authorized to get an API with tag " + tag), e);
            }
        }
    } catch (org.wso2.carbon.registry.core.exceptions.RegistryException e) {
        handleException(("Failed to get API for tag " + tag), e);
    } finally {
        if (isTenantFlowStarted) {
            org.wso2.carbon.context.PrivilegedCarbonContext.endTenantFlow();
        }
    }
    return apiSet;
}

