		for(int i=0;i<len;){
			int flag=a[i];int count=0;
			while(flag==a[i]){
				count++;i++;
				if(i==len) break;
			}
			if(count>=2) count1++;
			else if(count==1) count2++;	
		}
		count1=count1+count2/2; 
		cout<<count1<<endl;
	}
	return 0;
}

#include <cstdio>
#define swap(a, b) { ll t = a; a = b; b = t; }
typedef long long ll;
ll gcd(ll a, ll b) {
	if (a < b) swap(a, b);
	return b ? gcd(b, a % b) : a;
}
int main(void)
{
	int n;
	scanf("%d", &n);
	while (n--) {
		ll p, q, b;
		scanf("%lld %lld %lld", &p, &q, &b);
		ll g = gcd(p, q);
		p /= g, q /= g;
		if (p == 0 || q == 1) printf("Finite\n");
		else {
			g = gcd(q, b);
			while (g != 1) {
				while (!(q % g)) q /= g;
				while (!(b % g)) b /= g;
				g = gcd(q, b);
			}
			if (q == 1) printf("Finite\n");
			else printf("Infinite\n");
		}
	}
}

