struct Data{
	int u,p,q;
	int64_t w;
	Data(){}
	Data(int u,int p,int q,int64_t w):u(u),p(p),q(q),w(w){}
	bool operator<(const Data&other)const{
		return w<other.w;
	}
	bool operator>(const Data&other)const{
		return w>other.w;
	}
};
priority_queue<Data,vector<Data>,greater<Data>> Prq;
bool Mini(int64_t&x,int64_t const&y){
	if(y<x){
		x=y;
		return 1;
	}
	return 0;
}

int main(){
	cin.tie(nullptr)->sync_with_stdio(false);
	freopen("t.inp","r",stdin);
	freopen("t.out","w",stdout);
