		cin>>u>>v>>w;
		Adj[u].push_back(Edge(w,v));
		Adj[v].push_back(Edge(w,u));
	}
	memset(F,0x3f,sizeof F);
	F[1][0][0]=0;
	Prq.push(Data(1,0,0,0));
	while(!Prq.empty()){
		int u=Prq.top().u;
		int p=Prq.top().p;
