			if(Mini(F[v][p][q],x+w)){
				Prq.push(Data(v,p,q,F[v][p][q]));
			}
			if(!p&&Mini(F[v][!p][q],x)){
				Prq.push(Data(v,!p,q,F[v][!p][q]));
			}
			if(!q&&Mini(F[v][p][!q],x+2*w)){
				Prq.push(Data(v,p,!q,F[v][p][!q]));
			}
			if(!p&&!q&&Mini(F[v][!p][!q],x+w)){
				Prq.push(Data(v,!p,!q,F[v][!p][!q]));
			}
		}
	}
