	cin >> t;
	vector<int> nums(t);
	for (int i = 0; i < t; i++)
	{
		cin >> nums[i];
		maxN = max(maxN, nums[i]);
	}
	vector<int> dp(maxN + 1);
	int k = 2;
	int h = 1;
	for (int i = 2; i <= maxN; i++)
	{
		while (i >= k + h + h * 2 + 2)
		{
			k += h + h * 2 + 2;
			h++;
