const int N = 2005;

int n, k;
int arr[N];
int g[N];

bool ok(int p) {
  multiset <int> ms;
  for (int i = 0; i < k; i++) {
    ms.insert(g[i]);
  }
  for (int i = 0; i < n; i++) {
    p += arr[i];
    if (ms.find(p) != ms.end()) {
      ms.erase(ms.find(p));
    }
  }
  return ms.empty();
}

int main() {
  scanf("%d %d", &n, &k);
  for (int i = 0; i < n; i++) {
    scanf("%d", arr + i);
  }
  for (int i = 0; i < k; i++) {
    scanf("%d", g + i);
  }
  vector <int> vec;
  int pref[N];
  pref[0] = arr[0];
  for (int i = 1; i < n; i++) {
    pref[i] = pref[i - 1] + arr[i];
  }
  for (int i = 0; i < k; i++) {
    for (int j = 0; j < n; j++) {
      vec.push_back(g[i] + pref[j]);
      vec.push_back(g[i] - pref[j]);
    }
  }
  int ans = 0;
  map <int, bool> used;
  for (int i = 0; i < vec.size(); i++) {
    if (!used[vec[i]]) {
      used[vec[i]] = 1;
      ans += ok(vec[i]);
    }
  }
  printf("%d\n", ans);
  return 0;
}

