int dp[25000];
int	xcor[25000];
int ycor;
int n, k;
int main()
{
	int t;
	std::cin >> t;
	int first = 0;
	int sol = 0;
	while (t--)
	{
		std::cin >> n >> k;
		memset(dp, 0, sizeof dp);
		first = 0;
		sol = 0;
		for (int i = 0; i < n; i++)
		{
			std::cin >> xcor[i];
		}
		for (int i = 0; i < n; i++)
		{
			std::cin >> ycor;
		}
		std::sort(xcor, xcor + n);
		for (int i = 0; i < n; i++)
		{
			while (xcor[i] - xcor[first] > k) first++;
			dp[0] = 1;
			if (i > 0)
			{
				dp[i] = std::max(dp[i - 1], (i - first + 1));
			}
			if (first != 0)
			{
				sol = std::max(sol, (i - first + 1) + dp[first - 1]);
			}
			else
			{
				sol = i+1;
			}
		}
		std::cout << sol << std::endl;
	}

}

