#include <bits/stdc++.h>

using namespace std;

//#define ll long long

typedef long long ll;
typedef long double db;

const ll inf = 1000000000000000001;



struct node {
    ll l, r;
    bool need;
    char add;
    bool b1, b0;
    int left, right;
};

int pos = 0;
node root[3000003];

void upd(int now){
    if(root[root[now].left].b0 || root[root[now].right].b0)root[now].b0 = 1;
    else root[now].b0 = 0;
    if(root[root[now].left].b1 || root[root[now].right].b1)root[now].b1 = 1;
    else root[now].b1 = 0;
}

void push(int now){
    if(root[now].add != -1){
        root[root[now].left].need = 0;
        root[root[now].right].need = 0;
        root[root[now].left].add = root[now].add;
        root[root[now].right].add = root[now].add;
        if(root[now].add == 1){
            root[root[now].left].b1 = root[root[now].right].b1 = 1;
            root[root[now].left].b0 = root[root[now].right].b0 = 0;
        }
        else {
            root[root[now].left].b1 = root[root[now].right].b1 = 0;
            root[root[now].left].b0 = root[root[now].right].b0 = 1;
        }
        root[now].add = -1;
    }

    if(root[now].need){
        root[root[now].left].need = !root[root[now].left].need;
        root[root[now].right].need = !root[root[now].right].need;
        swap(root[root[now].left].b0, root[root[now].left].b1);
        swap(root[root[now].right].b0, root[root[now].right].b1);
        root[now].need = 0;
        //swap(root[now].b0, root[now].b1);
    }
}

void update(int now, ll l, ll r, ll val){
    if(root[now].l > r || root[now].r < l)return;
    if(root[now].l >= l && root[now].r <= r){
        root[now].need = 0;
        root[now].add = val;
        if(val == 1){
            root[now].b1 = 1;
            root[now].b0 = 0;
        }
        else {
            root[now].b0 = 1;
            root[now].b1 = 0;
        }
        return;
    }

    ll mid = (root[now].l + root[now].r) / 2;
    if(root[now].left == -1){
        root[now].left = pos++;
        root[root[now].left].b0 = 1;
        root[root[now].left].b1 = 0;
        root[root[now].left].l = root[now].l;
        root[root[now].left].r = mid;
        root[root[now].left].left = root[root[now].left].right = -1;
        root[root[now].left].need = 0;
        root[root[now].left].add = -1;
    }

    if(root[now].right == -1){
        root[now].right = pos++;
        root[root[now].right].b0 = 1;
        root[root[now].right].b1 = 0;
        root[root[now].right].l = mid+1;
        root[root[now].right].r = root[now].r;
        root[root[now].right].left = root[root[now].right].right = -1;
        root[root[now].right].need = 0;
        root[root[now].right].add = -1;
    }

    push(now);
    update(root[now].left, l, r, val);
    update(root[now].right, l, r, val);
    upd(now);
}

void update2(int now, ll l, ll r){
    if(root[now].l > r || root[now].r < l)return;
    if(root[now].l >= l && root[now].r <= r){
        root[now].need = !root[now].need;
        //upd(root);
        swap(root[now].b0, root[now].b1);
        return;
    }

    ll mid = (root[now].l + root[now].r) / 2;
    if(root[now].left == -1){
        root[now].left = pos++;
        root[root[now].left].b0 = 1;
        root[root[now].left].b1 = 0;
        root[root[now].left].l = root[now].l;
        root[root[now].left].r = mid;
        root[root[now].left].left = root[root[now].left].right = -1;
        root[root[now].left].need = 0;
        root[root[now].left].add = -1;
    }

    if(root[now].right == -1){
        root[now].right = pos++;
        root[root[now].right].b0 = 1;
        root[root[now].right].b1 = 0;
        root[root[now].right].l = mid+1;
        root[root[now].right].r = root[now].r;
        root[root[now].right].left = root[root[now].right].right = -1;
        root[root[now].right].need = 0;
        root[root[now].right].add = -1;
    }

    push(now);
    update2(root[now].left, l, r);
    update2(root[now].right, l, r);
    upd(now);
}

ll query(int now){
    if(root[now].left == -1)return root[now].l;
    push(now);
    if(root[root[now].left].b0){
        return query(root[now].left);
    }
    else {
        return query(root[now].right);
    }
}

